import React, { useContext, useEffect } from 'react'
import './home.css';
import VendingMachine from '../assests/images/vendingMachine1.png';
import { Context as MainContext } from '../context/mainContext';
import { ListGroup, ListGroupItem, Badge ,Card, Button, CardTitle, CardText} from 'reactstrap';

const Home = () => {

  const { state: { items }, getItems, getCredits } = useContext<any>(MainContext);

  useEffect(() => {
    getItems();
    getCredits();
  }, [])

  return (
    <div className="home">
      <div className="home_left">
        <p>Welcome To our Vending Machine</p>
        <p>Explore our drinks and Enjoy!</p>
        <Card body>
        <CardTitle tag="h5">Special Title Treatment</CardTitle>
        <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
        <Button>Go somewhere</Button>
      </Card>
      </div>
      <div className="home_center">
        <img src={VendingMachine} alt="Vending Machine" />
      </div>
      <div className="home_right">
        <ListGroup>
          {
            items.map((item: any) => {
              return (
                <><ListGroupItem className="justify-content-between">{item.name} <Badge pill>{item.stock}</Badge></ListGroupItem>
                </>
              )
            })
          }
        </ListGroup>
      </div>
    </div>
  )
}

export { Home };
