import React, { useContext, useEffect } from 'react'
import './home.css';
import VendingMachine from '../assests/images/vendingMachine1.png';
import { Context as MainContext } from '../context/mainContext';
import { ListGroup, ListGroupItem, Badge, Card, Button, CardTitle, CardText } from 'reactstrap';

const Home = () => {

  const { state: { items }, getItems, getCredits } = useContext<any>(MainContext);

  useEffect(() => {
    getItems();
    getCredits();
  }, [])

  return (
    <div className="home">
      <div className="home_left">
        <Card body>
          <CardTitle tag="h5">Welcome To our Vending Machine</CardTitle>
          <CardText>Explore our drinks and Enjoy</CardText>
          <Button>Go somewhere</Button>
        </Card>
      </div>
      <div className="home_center">
        <img src={VendingMachine} alt="Vending Machine" />
      </div>
      <div className="home_right">
        <ListGroup>
          {
            items.map((item: any) => {
              return (
                <><ListGroupItem className="justify-content-between">{item.name} <p></p>
                  <Badge pill style={{ backgroundColor: item.stock > 0 ? 'grey' : 'red', color: "white", marginLeft: 10 }}>{item.stock}</Badge>
                </ListGroupItem>
                </>
              )
            })
          }
        </ListGroup>
      </div>
    </div>
  )
}

export { Home };
