import React, { useContext, useState } from 'react';
import { Context as MainContext } from '../../context/mainContext'
import './style.css';
import {
  Card, Button, CardHeader, CardFooter, CardBody,
  FormGroup, Input,
  Col, Badge
} from 'reactstrap';
import Coke from '../../assests/images/coca-cola.jpg';
import Dew from '../../assests/images/dew.jpg';
import Pepsi from '../../assests/images/pepsi.jpeg';
import color from '../../assests/colors';

export const ProductList = () => {


  const [itemState, setItem] = useState<any>({
    coke: { quantity: '', totalPrice: '', price: '' },
    dew: { quantity: '', totalPrice: '', price: '' },
    pepsi: { quantity: '', totalPrice: '', price: '' }
  })

  const renderImage = (item: string) => {
    switch (item) {
      case 'dew':
        return <img src={Dew} alt="dew" className="item_list" />
      case 'coke':
        return <img src={Coke} alt="coke" className="item_list" />
      case 'pepsi':
        return <img src={Pepsi} alt="pepsi" className="item_list" />
      default:
        return null;
    }
  }

  const handleChange = (value: any, item: any, key: string) => {
    console.log('handleChange', value, key)
    switch (key) {
      case 'quantity': setQuantity(value);
        setTotalPrice(value * item.price)
        break;
      case 'price': setPrice(value);
        break;
      default: return;
    }
  }

  const renderItem = (item: any, index: number) => {
    return <Card key={index} className="item">
      <CardHeader>
        <div className="item-header">
          <div>
            {item.name}
            <p
              style={{
                display: 'inline',
                padding: 9,
                fontWeight: 'bold'
              }}>
              Rs{item.price}
            </p>
          </div>
          <div>
            Stock
            <Badge color="primary" >{item.stock}</Badge>
          </div>
        </div>
      </CardHeader>
      <CardBody>
        {renderImage(item.name.toLowerCase())}
        <div className='input-wrapper'>
          <FormGroup row>
            <Col sm={10}>
              <Input
                type="number"
                name="qty"
                value={item.coke.quantity}
                placeholder="quantity"
                onChange={(event) => handleChange(event.target.value, item, 'quantity')}
              />
            </Col>
          </FormGroup>
          <FormGroup row>
            <Col sm={10}>
              <Input type="text"
                name="text"
                disabled={true}
                value={item[ite]}
                placeholder="total price"
              />
            </Col>
          </FormGroup>
          <FormGroup row>
            <Col sm={10}>
              <Input type="text"
                name="text"
                value={price}
                placeholder="price"
                onChange={(event) => handleChange(event.target.value, item, 'price')}
              />
            </Col>
          </FormGroup>
        </div>

      </CardBody>
      <CardFooter>
        <Button className="buyBtn">Buy</Button>
      </CardFooter>
    </Card>
  }

  const { state: { items } } = useContext<any>(MainContext);
  return (
    <div className="product-list">
      {
        items.map((item: any, index: number) => {
          return renderItem(item, index)
        })
      }
    </div>
  )
}
