import React, { useContext, useState } from 'react';
import { Context as MainContext } from '../../context/mainContext'
import './style.css';
import {
  Card, Button, CardHeader, CardFooter, CardBody,
  FormGroup, Input,
  Col, Badge
} from 'reactstrap';
import Coke from '../../assests/images/coca-cola.jpg';
import Dew from '../../assests/images/dew.jpg';
import Pepsi from '../../assests/images/pepsi.jpeg';
import colors from '../../assests/colors';
import { AlertInfo } from '../../common';

export const ProductList = () => {


  const [itemState, setItemState] = useState<any>({
    coke: { quantity: '', totalPrice: '', price: '', disabled: true ,itemId:''},
    dew: { quantity: '', totalPrice: '', price: '', disabled: true },
    pepsi: { quantity: '', totalPrice: '', price: '', disabled: true }
  })

  const renderImage = (item: string) => {
    switch (item) {
      case 'dew':
        return <img src={Dew} alt="dew" className="item_list" />
      case 'coke':
        return <img src={Coke} alt="coke" className="item_list" />
      case 'pepsi':
        return <img src={Pepsi} alt="pepsi" className="item_list" />
      default:
        return null;
    }
  }

  const handleChange = (value: any, item: any, key: string) => {
    console.log('handleChange', value, key)
    const name = item.name.toLowerCase()
    switch (key) {
      case 'quantity': setItemState({
        ...itemState,
        [name]: {
          ...itemState[name],
          quantity: value,
          totalPrice: value * item.price,
          disabled: (itemState[name].price && itemState[name].quantity) ? false : true
        }
      });
        break;
      case 'price': setItemState({
        ...itemState,
        [name]: {
          ...itemState[name],
          price: value,
          disabled: (itemState[name].price && itemState[name].quantity) ? false : true
        }
      });;
        break;
      default: return;
    }
  }

  const handleBuy = (itemName: string) => {
    console.log('item', itemState[itemName.toLowerCase()])
  }

  const renderItem = (item: any, index: number) => {
    const name = item.name.toLowerCase();
    console.log(itemState)
    return <Card key={index} className="item">
      <CardHeader>
        <div className="item-header">
          <div>
            {item.name}
            <p
              style={{
                display: 'inline',
                padding: 9,
                fontWeight: 'bold'
              }}>
              Rs{item.price}
            </p>
          </div>
          <div>
            Stock
            <Badge style={{ backgroundColor: colors.lightBlue, padding: 6, marginLeft: 10 }} >{item.stock}</Badge>
          </div>
        </div>
      </CardHeader>
      <CardBody>
        {renderImage(item.name.toLowerCase())}
        <div className='input-wrapper'>
          <FormGroup row>
            <Col sm={10}>
              <Input
                type="number"
                name="qty"
                value={itemState[name].quantity}
                placeholder="quantity"
                onChange={(event) => handleChange(event.target.value, item, 'quantity')}
              />
            </Col>
          </FormGroup>
          <FormGroup row>
            <Col sm={10}>
              <Input type="text"
                name="text"
                disabled={true}
                value={itemState[name].totalPrice}
                placeholder="total price"
              />
            </Col>
          </FormGroup>
          <FormGroup row>
            <Col sm={10}>
              <Input type="number"
                name="text"
                value={itemState[name].price}
                placeholder="price"
                onChange={(event) => handleChange(event.target.value, item, 'price')}
              />
            </Col>
          </FormGroup>
        </div>

      </CardBody>
      <CardFooter>
        <Button
          style={{ width: '100%', backgroundColor: colors.lightBlue }}
          disabled={itemState[name].disabled}
          onClick={() => handleBuy(item.name)}
        >
          Buy
        </Button>
      </CardFooter>
    </Card>
  }

  const { state: { items } } = useContext<any>(MainContext);
  return (
    <>
      {/* <AlertInfo message="Product is bought successfully" /> */}
      <div className="product-list">

        {
          items.map((item: any, index: number) => {
            return renderItem(item, index)
          })
        }
      </div>
    </>
  )
}
