import createDataContext from './createDataContext';
import { instance as api } from '../api/api';

const mainReducer = (state: any, action: any) => {
  switch (action.type) {
    case 'FETCH_ITEMS':
      return { ...state, items: action.payload, loading: false };
    case 'FETCH_CREDIT':
      return { ...state, credit: action.payload, loading: false };
    case 'LOADING':
      return { ...state, loading: action.payload };
    default:
      return state;
  }
};

const getItems = (dispatch: any) => async () => {
  dispatch({ type: 'LOADING', payload: true });
  const { data } = await api.get('/items');
  dispatch({ type: 'FETCH_ITEMS', payload: data });
};

const getCredits = (dispatch: any) => async () => {
  try {
    dispatch({ type: 'LOADING', payload: true });
    const { data } = await api.get('/credits');
    console.log(data)
    dispatch({ type: 'FETCH_CREDIT', payload: data });
  } catch (error) {
    console.log('getCredits error', error)
  }
};


const buyItem = (dispatch: any) => async (item: any, callback: (success: boolean, msg?: string) => void) => {
  try {
    const response = await api.post('/items/buy', item);
    console.log('BuyItem response', response);
    callback(true);
  } catch (error) {
    console.log('BuyItem error', error)
    callback(false, error?.message ?? 'Something went wrong.');
  }
}

export const { Context, Provider } = createDataContext(
  mainReducer,
  { getItems, getCredits, buyItem },
  { loading: false, items: [], credit: [] }
);
