import React, { useState, useEffect } from 'react'
import { Alert } from 'reactstrap';

export const AlertInfo = ({ message, color = 'primary', show, setAlert }: { message: string, color?: string, show: boolean, setAlert?: (flag: boolean) => any }) => {
  useEffect(() => {
    setTimeout(() => {
      setAlert(false)
    }, 3000)
  }, [show])
  if (show) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', margin: 10 }}>
        <Alert color={color} style={{ width: 800 }}>
          {message}
        </Alert >
      </div>
    )
  } else return null
}
