import React, { useContext } from 'react';
import { Context as MainContext } from '../../context/mainContext'
import './style.css';
import {
  Card, Button, CardHeader, CardFooter, CardBody,
  CardTitle, CardText
} from 'reactstrap';
import Coke from '../../assests/images/coca-cola.jpg';
import Dew from '../../assests/images/dew.jpg';
import Pepsi from '../../assests/images/pepsi.jpeg';

export const ProductList = () => {

  const renderImage = (item: string) => {
    switch (item) {
      case 'dew':
        return <img src={Dew} alt="dew" />
      case 'coke':
        return <img src={Coke} alt="dew" />
      case 'pepsi':
        return <img src={Pepsi} alt="dew" />
      default:
        return null;
    }
  }
  const renderItem = (item: any, index: number) => {
    return <Card key={index} className="item">
      <CardHeader>{item.name}</CardHeader>
      <CardBody>
        {renderImage(item.name.toLowerCase())}
        {/* <CardTitle tag="h5">Special Title Treatment</CardTitle>
        <CardText>With supporting text below as a natural lead-
          in to additional content.</CardText> */}
        <Button>Buy</Button>
      </CardBody>
      <CardFooter>Footer</CardFooter>
    </Card>
  }

  const { state: { items } } = useContext<any>(MainContext);
  console.log('items', items)
  return (
    <div className="product-list">
      {
        items.map((item: any, index: number) => {
          return renderItem(item, index)
        })
      }
    </div>
  )
}
