import React, { useState, useEffect } from 'react'
import { Alert } from 'reactstrap';

export const AlertInfo = ({ message, color = 'primary' }: { message: string, color?: string }) => {
  const [show, setShow] = useState(true);
  useEffect(() => {
    setTimeout(() => {
      setShow(false)
    }, 3000)
  }, [])
  if (show) {
    return (
      <Alert color={color} style={{width}}>
        {message}
      </Alert >
    )
  } else return null
}
