import React, { useContext, useEffect } from 'react'
import './home.css';
import VendingMachine from '../assests/images/vendingMachine1.png';
import { Context as MainContext } from '../context/mainContext';
import { ListGroup, ListGroupItem, Badge } from 'reactstrap';

const Home = () => {

  const { getItems, getCredits } = useContext<any>(MainContext);

  useEffect(() => {
    getItems();
    getCredits();
  }, [])

  return (
    <div className="home">
      <div className="home_left">
        <p>Welcome To our Vending Machine</p>
        <p>Explore our drinks and Enjoy!</p>
      </div>
      <div className="home_center">
        <img src={VendingMachine} alt="Vending Machine" />
      </div>
      <div className="home_right">
        <ListGroup>
          <ListGroupItem className="justify-content-between">Cras justo odio <Badge pill>14</Badge></ListGroupItem>
          <ListGroupItem className="justify-content-between">Dapibus ac facilisis in <Badge pill>2</Badge></ListGroupItem>
          <ListGroupItem className="justify-content-between">Morbi leo risus <Badge pill>1</Badge></ListGroupItem>
        </ListGroup>
      </div>
    </div>
  )
}

export { Home };
