import { Alert } from 'reactstrap';

export const AlertInfo = ({ message, color = 'primary' }: { message: string, color?: string }) => {
  const [show, setShow] = useState(true);
  useEffect(() => {
    setTimeout(() => {
      setShow(false)
    }, 2000)
  }, [])
  return (
  {
    show
  }
  )
}
